    /**  验证  
    * （目前只支持input标签）
    * @param form 验证的对象
    */
    worf.validate = function (form, options) {
        var $form = $(form);
        var isOK = true;
        var valid = function (el) {
            var that = $(el);
            //检查验证标记
            if (!that.data("val")) return true;
            var value = $.trim(that.val());
            var name = that.attr("name");
            var lableName = $form.find("[for='" + name + "']").html().replace(/[\:：\*]/ig, "") || "！未知！";
            //必填验证
            var requiredMessage = that.data("valRequiredMs");
            if (value == "") {
                worf.prompt.tip(requiredMessage || ("请输入" + lableName));
                isOK = false;
                return false;
            }
            //正则验证
            var regex = that.data("valRegex");
            var regexMessage = that.data("valRegexMs");
            if (regex && !new RegExp(regex, "ig").test(value)) {
                worf.prompt.tip(regexMessage || (lableName + "格式不正确"));
                isOK = false;
                return false;
            }
            return isOK;
        }
        $form.find("input[data-val]").each(function () {
            if (!valid(this)) return false;
        });
        return isOK;
    };

