(function ($) {
    /*
    * 全局处理
    * */
    $(function () {
        //跳转
        $(document).on("click", "[data-href]", function () {
            //获取data-href跳转
            var dataHrefs = $(this).data("href");
            if (dataHrefs == "") {
                return;
            } else {
                $("section").children().find(".videos").css({ "right": "0rem" });
                $("section").children().find(".seles").css({ "right": "-1rem" });

                $("#isAllCourse").height(0);
                worf.nav.go(dataHrefs);
            }
        });
        //返回
        $(".back").off("click").on("click", function () {
            if (this.dataset.special) {
                this.dataset.special != "true" && eval(this.dataset.special);
                return;
            }
            worf.nav.back();
        }); 
    });
})(Zepto);
/**********************
 * app init
 ***********************/
(function ($) {
    worf.app = {
        isReady: false,
        //注册app加载完成事件
        ready: function (callback) {
            if (worf.app.isReady) { //已经加载完成的直接调用
                callback();
            } else {
                $(document).on("deviceready", callback);
            }
        },
        //暂停
        pause: function (callback) {
            $(document).on("pause", callback);
        },
        //恢复
        resume: function (callback) {
            $(document).on("resume", callback);
        },
        //app接口参数,对应app.goApp(?)
        goType: { login: 1 },
        /*
         * 切换app的标题
         * @param options :{ title:"", rightTitle:"" },rightTitle控制右上角文本
         */
        toogleTitle: function (current, options) {
            var title = typeof (current) == "string" ? current : $(current).html();
            document.title = title;
            if (worf.app.isReady && app.setTitleBar) {
                var args = $.extend({ title: title }, options || {});
                app.setTitleBar(JSON.stringify(args));
            }
        },
        //回到app首页
        toIndex: function (fromPage, closeMe) {
            if (worf.app.isReady && app.toIndex) {
                var data = { from: fromPage, closeMe: closeMe };
                app.toIndex(JSON.stringify(data));
            }
        },
        //退出APP删除cookie
        exitApp: function () {
            worf.localStorage.del("tabid");
        },
        //获取缓存图片地址
        getCacheImage:function(url){
        	return url+"#cache";
        }
    };

    //app加载完成
    worf.app.ready(function () {
        worf.app.isReady = true;
    });

    worf.app.pause(function () {
    });

    worf.app.resume(function () {
        window.loadData && window.loadData();
    });
    (function (app) {
        var cacheId = 1;
        app.getImage = function (src, callback) {
            var callbackId = "getImageCallBack" + cacheId;
            window[callbackId] = function (url) {
                callback && callback(url);
            }
            cacheId++;
            window.app.getImage(JSON.stringify({ url: src, callback: callbackId }));
        }

    })(worf.app);

    //app调用web接口
    worf.web = {
        //点击头部右侧按钮（供app调用js的返回）
        rightClick: function () {
            GlobalFastClick.trackingClick = false;
            GlobalFastClick.targetElement = null;
            var showBack = $(".header_right[data-show='true']");
            if (showBack.length > 0) {
                showBack.trigger("click");
            } else {
                $(".header_right").eq(0).trigger("click");
            }
        },
        //返回
        goBack: function () {
            if (worf.nav.currentIsHome()) {
                window.goback && window.goback();
            } else {
                GlobalFastClick.trackingClick = false;
                GlobalFastClick.targetElement = null;
                var showBack = $(".back[data-show='true']");
                if (showBack.length > 0) {
                    showBack.trigger("click");
                } else {
                    $(".back").eq(0).trigger("click");
                }
            }
        }
    };
})(Zepto);